import React, {useState} from 'react';
import axios from 'axios';
import WeatherHistoryCard from '../components/card-component/WeatherHistoryCard';

const WeatherHistory = () => {

  const [weatherHistoryData, setWeatherHistoryData] = useState({});
  const [userLocation, setUserLocation] = useState('');

  const apiKey = 'b739f9dce98a4420b36175641221102';

  const baseUrl = `http://api.weatherapi.com/v1/history.json?key=${apiKey}&q=${userLocation}&aqi=no`;

  const handleChange = (e) => {
    setUserLocation(e.target.value);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .get(baseUrl)
      .then((info) => {
        setWeatherHistoryData(info.data);
        console.log(weatherHistoryData);
      })
      .catch((err) => {
        console.log("NOOOOO!!!!", err.message);
      });
    }



  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input className='location-input'  onChange={handleChange}/>
        <button className='button-search' placeholder='format: yyyy/mm/dd'>Search by date</button>
      </form>
      <br/>

      <h1>Weather History</h1>
      { weatherHistoryData === [] || weatherHistoryData === undefined ? <div></div> : 
        <div>
      <WeatherHistoryCard />
    </div>
}
    </div>
  )
}

export default WeatherHistory;
