import React, {useState} from 'react';
import axios from 'axios';
import CurrentWeatherCard from '../components/card-component/CurrentWeatherCard';

const Home = () => {

  const [weatherData, setWeatherData] = useState([]);
  const [userLocation, setUserLocation] = useState('');

  const apiKey = 'b739f9dce98a4420b36175641221102';

  const baseUrl = `http://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${userLocation}&aqi=no`;

  const handleChange = (e) => {
    setUserLocation(e.target.value);
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .get(baseUrl)
      .then((info) => {
        setWeatherData(info.data);
        console.log(weatherData.location.name);
      })
      .catch((err) => {
        console.log("NOOOOO!!!!", err.message);
      });
    }



  return (
    <div>
      <h1>Current Weather reached</h1>
      <form onSubmit={handleSubmit}>
        <input className='location-input'  onChange={handleChange}/>
        <button className='button-search'>Search</button>
      </form>
      { weatherData === [] || weatherData === undefined ? <div></div> : 
        <div>
      <CurrentWeatherCard cityName={weatherData.location.name}/>
    </div>
}
    </div>
  )
}

export default Home;
